mapper.select:
select g.GOOD_ID as detectSto_goodsID, g.GOOD_NAME as detectSto_goodsName, b.REPO_BATCH_ID as detectSto_batchID, b.REPO_BATCH_CODE as detectSto_batchCode, r.REPO_ID as detectSto_repositoryID, ds.DETECT_NUMBER as detectSto_number, ds.DETECT_PASSED as detectSto_passed, ds.DETECT_SCRATCH as detectSto_scratch, ds.DETECT_DAMAGE as detectSto_damage from wms_detect_storage ds, wms_respository r, wms_repo_batch b, wms_goods g where ds.DETECT_GOODID = g.GOOD_ID and ds.DETECT_BATCHID = b.REPO_BATCH_ID and ds.DETECT_REPOSITORY = r.REPO_ID
and b.REPO_BATCH_ID = #{batchID}
and r.REPO_ID = #{repositoryID}

select g.GOOD_ID as detectSto_goodsID, g.GOOD_NAME as detectSto_goodsName, b.REPO_BATCH_ID as detectSto_batchID, b.REPO_BATCH_CODE as detectSto_batchCode, r.REPO_ID as detectSto_repositoryID, ds.DETECT_NUMBER as detectSto_number, ds.DETECT_PASSED as detectSto_passed, ds.DETECT_SCRATCH as detectSto_scratch, ds.DETECT_DAMAGE as detectSto_damage from wms_detect_storage ds, wms_respository r, wms_repo_batch b, wms_goods g where ds.DETECT_GOODID = g.GOOD_ID and ds.DETECT_BATCHID = b.REPO_BATCH_ID and ds.DETECT_REPOSITORY = r.REPO_ID and g.GOOD_ID = #{goodsID}
and b.REPO_BATCH_ID = #{batchID}
and r.REPO_ID = #{repositoryID}
