mapper.select:
select ds.DETECT_GOODID as detectSto_goodsID, ds.DETECT_BATCHID as detectSto_batchID, ds.DETECT_REPOSITORY as detectSto_repositoryID, ds.DETECT_NUMBER as detectSto_number, ds.DETECT_PASSED as detectSto_passed, ds.DETECT_SCRATCH as detectSto_scratch, ds.DETECT_DAMAGE as detectSto_damage, g.GOOD_NAME as detectSto_goodsName, b.REPO_BATCH_CODE as detectSto_batchCode from wms_detect_storage ds, wms_repo_batch b, wms_goods g where ds.DETECT_GOODID = g.GOOD_ID and ds.DETECT_BATCHID = b.REPO_BATCH_ID
and ds.DETECT_BATCHID = #{batchID}
and ds.DETECT_REPOSITORY = #{repositoryID}

select ds.DETECT_GOODID as detectSto_goodsID, ds.DETECT_BATCHID as detectSto_batchID, ds.DETECT_REPOSITORY as detectSto_repositoryID, ds.DETECT_NUMBER as detectSto_number, ds.DETECT_PASSED as detectSto_passed, ds.DETECT_SCRATCH as detectSto_scratch, ds.DETECT_DAMAGE as detectSto_damage, g.GOOD_NAME as detectSto_goodsName, b.REPO_BATCH_CODE as detectSto_batchCode from wms_detect_storage ds, wms_repo_batch b, wms_goods g where ds.DETECT_GOODID = g.GOOD_ID and ds.DETECT_BATCHID = b.REPO_BATCH_ID and ds.DETECT_GOODID = #{goodsID}
and ds.DETECT_BATCHID = #{batchID}
and ds.DETECT_REPOSITORY = #{repositoryID}

select ds.DETECT_GOODID as detectSto_goodsID, ds.DETECT_BATCHID as detectSto_batchID, ds.DETECT_REPOSITORY as detectSto_repositoryID, ds.DETECT_NUMBER as detectSto_number, ds.DETECT_PASSED as detectSto_passed, ds.DETECT_SCRATCH as detectSto_scratch, ds.DETECT_DAMAGE as detectSto_damage, g.GOOD_NAME as detectSto_goodsName, b.REPO_BATCH_CODE as detectSto_batchCode from wms_detect_storage ds, wms_repo_batch b, wms_goods g where ds.DETECT_GOODID = g.GOOD_ID and ds.DETECT_BATCHID = b.REPO_BATCH_ID and g.GOOD_NAME like concat("%",concat(#{goodsName},"%"))
and ds.DETECT_BATCHID = #{batchID}
and ds.DETECT_REPOSITORY = #{repositoryID}

mapper.update:
update wms_detect_storage set DETECT_GOODID = #{goodsID}, DETECT_BATCHID = #{batchID}, DETECT_REPOSITORY = #{repositoryID}, DETECT_NUMBER = #{number}, DETECT_PASSED = #{passed}
, DETECT_SCRATCH = #{scratch}
, DETECT_DAMAGE = #{damage}
where DETECT_GOODID = #{goodsID} and DETECT_BATCHID = #{batchID} and DETECT_REPOSITORY = #{repositoryID}
