mapper.select:
select PACKET_ID, PACKET_TRACE, PACKET_TIME, PACKET_STATUS, PACKET_DESC, PACKET_REPOID from wms_packet where PACKET_TRACE like concat ('%',#{trace},'%')
and PACKET_STATUS = #{status}
and PACKET_REPOID = #{repositoryID}

mapper.update:
update
	wms_packet
set
	PACKET_TRACE = #{trace},
	PACKET_TIME = #{time},
	PACKET_STATUS = #{status}
	PACKET_DESC = #{desc},
	PACKET_REPOID = #{repositoryID}
where
	PACKET_ID = #{id}
