mapper.select:
select ps.PRE_PACKETID as preStorage_packetID, ps.PRE_GOODID as preStorage_goodsID, ps.PRE_CUSTOMERID as preStorage_customerID, ps.PRE_REPOSITORYID as preStorage_repositoryID, ps.PRE_NUMBER as preStorage_number, ps.PRE_STORAGE as preStorage_storage, p.PACKET_TRACE as preStorage_packetTrace, p.PACKET_STATUS as preStorage_packetStatus, g.GOOD_NAME as preStorage_goodsName, cus.CUSTOMER_NAME as preStorage_customerName from wms_packet_storage ps,wms_packet p, wms_goods g, wms_customer cus, wms_repository r where ps.PRE_PACKETID = p.PACKET_ID and ps.PRE_GOODID = g.GOOD_ID and ps.PRE_CUSTOMERID = cus.CUSTOMER_ID and ps.PRE_REPOSITORYID = r.REPO_ID
and ps.PRE_PACKETID = #{packetID}
and p.PACKET_STATUS = #{status}
and ps.PRE_REPOSITORYID = #{repositoryID}

select ps.PRE_PACKETID as preStorage_packetID, ps.PRE_GOODID as preStorage_goodsID, ps.PRE_CUSTOMERID as preStorage_customerID, ps.PRE_REPOSITORYID as preStorage_repositoryID, ps.PRE_NUMBER as preStorage_number, ps.PRE_STORAGE as preStorage_storage, p.PACKET_TRACE as preStorage_packetTrace, p.PACKET_STATUS as preStorage_packetStatus, g.GOOD_NAME as preStorage_goodsName, cus.CUSTOMER_NAME as preStorage_customerName from wms_packet_storage ps,wms_packet p, wms_goods g, wms_customer cus, wms_repository r where ps.PRE_PACKETID = p.PACKET_ID and ps.PRE_GOODID = g.GOOD_ID and ps.PRE_CUSTOMERID = cus.CUSTOMER_ID and ps.PRE_REPOSITORYID = r.REPO_ID
and ps.PRE_GOODID = #{goodsID}
and ps.PRE_PACKETID = #{packetID}
and ps.PRE_REPOSITORYID = #{repositoryID}

select ps.PRE_PACKETID as preStorage_packetID, ps.PRE_GOODID as preStorage_goodsID, ps.PRE_CUSTOMERID as preStorage_customerID, ps.PRE_REPOSITORYID as preStorage_repositoryID, ps.PRE_NUMBER as preStorage_number, ps.PRE_STORAGE as preStorage_storage, p.PACKET_TRACE as preStorage_packetTrace, p.PACKET_STATUS as preStorage_packetStatus, g.GOOD_NAME as preStorage_goodsName, cus.CUSTOMER_NAME as preStorage_customerName from wms_packet_storage ps,wms_packet p, wms_goods g, wms_customer cus, wms_repository r where ps.PRE_PACKETID = p.PACKET_ID and ps.PRE_GOODID = g.GOOD_ID and ps.PRE_CUSTOMERID = cus.CUSTOMER_ID and ps.PRE_REPOSITORYID = r.REPO_ID
and p.PACKET_TRACE like concat ('%',#{trace},'%')
and p.PACKET_STATUS = #{status}
and ps.PRE_REPOSITORYID = #{repositoryID}

mapper.update:
update 
	wms_packet_storage
set
	PRE_NUMBER = #{number},
	PRE_STORAGE = #{storage}
where
	PRE_PACKETID = #{packetID} and
	PRE_GOODID = #{goodsID}

	and PRE_REPOSITORYID = #{repositoryID}

mapper.delete:
delete from wms_packet_storage
where
	PRE_PACKETID = #{packetID} and
	PRE_GOODID = #{goodsID}

	and PRE_REPOSITORYID = #{repositoryID}
